mutation Users {
    users {
        create(
            user: {
                 id: "{{userId}}"
                first_name: "{{firstName}}"
                last_name: "{{lastName}}"
                email: "{{emailId}}"
            }
        ) {
            id
            first_name
            last_name
            email
        }
